for(int i = 0; i < numberOfRequest; i++){	
	if(sortedBuffer.get(i).inState(sortedBuffer.get(i).searching)){
		deliver(String.valueOf(vehicleId) + "Coming", sortedBuffer.get(i));
		traceln("Accepted rider " + sortedBuffer.get(i).riderId +"'s request");
		
		this.curCapacity -= 1;
		this.myRides.add(sortedBuffer.get(i));
		nextMove();
