//Clearing the buffer
buffer.clear();

if(buffer.size() != 0 && myRides.size() != 0){
	double maxLat = this.getLatitude();
	double maxLon = this.getLongitude();
	
	double minLat = this.getLatitude();
	double minLon = this.getLongitude();
	
	//traceln("Latitude and Long of vehicle: " + this.getLatitude() + this.getLongitude());
	
	for(RideRequest rr: myRides){
		//traceln("Latitude and Long of riders pickup: " + rr.puLat + " " + rr.puLon);
		//traceln("Latitude and Long of riders dropof: " + rr.doLat + " " + rr.doLon);
		
		maxLat = Math.max(maxLat, rr.puLat);
		maxLat = Math.max(maxLat, rr.doLat);
		maxLon = Math.max(maxLon, rr.puLon);
		maxLon = Math.max(maxLon, rr.doLon);
		
		
		minLat = Math.min(minLat, rr.puLat);
		minLat = Math.min(minLat, rr.doLat);
		minLon = Math.min(minLon, rr.puLon);
		minLon = Math.min(minLon, rr.doLon);
	}
	
	int i = 0;
	List<Integer> toRemove = new ArrayList();
	for(RideRequest rr: this.buffer){
		// Pickup is in our bounding box
		if(!(rr.puLat <= maxLat && rr.puLat >= minLat && rr.puLon <= maxLon && rr.puLon >= minLon)){
			toRemove.add(i);
			i++;
			continue;
		}
		// dropof location is in our bounding box
		if(!(rr.doLat <= maxLat && rr.doLat >= minLat && rr.doLon <= maxLon && rr.doLon >= minLon)){
			toRemove.add(i);
			i++;
			continue;	
		}
		i++;
	}
	this.buffer.removeAll(toRemove);
	//traceln("max latitude and Long points: " + maxLat + " " + maxLon);
	//traceln("max latitude and Long points: " + minLat + " " + minLon);
}
goToState = "processRequests";

