Function.Body:
traceln("myRides: " + myRides);
traceln("PickedUp rides: " + pickedUpRides);

for(RideRequest rr: myRides){
	if(pickedUpRides.contains(rr)){
		GISPoint dropOff = new GISPoint(main.map, rr.doLat, rr.doLon);
		nextPossibleDestinations.add(dropOff);
	}else{
		GISPoint pickUp = new GISPoint(main.map, rr.puLat, rr.puLon);
		nextPossibleDestinations.add(pickUp);
	}
}	

int indexOfNearestPoint = 0;
int i = 0;
GISPoint nearestPoint = nextPossibleDestinations.get(0);


traceln(nextPossibleDestinations);
for(GISPoint p: nextPossibleDestinations){
	if(this.distanceTo(p.getLatitude(), p.getLongitude()) < this.distanceTo(nearestPoint.getLatitude(), nearestPoint.getLongitude())){
		nearestPoint = p;
		indexOfNearestPoint = i;
	}
	i++;
}	

this.nextDestination = nearestPoint;		// This is a point
this.currServing = myRides.get(indexOfNearestPoint);   // This is the reference to the ride we are serving
traceln("Serving: " + currServing);
if(pickedUpRides.contains(currServing)){
	act = "dropping";
}else{
	act = "pickingup";
}

this.moveTo(nearestPoint.getLatitude(), nearestPoint.getLongitude());
this.nextPossibleDestinations.clear();

for(int i = 0; i < myRides.size(); i++){
	if(myRides.get(i) == ride){
		return i;
	}
}
return -1;

for(int i = 0; i < pickedUpRides.size(); i++){
	if(pickedUpRides.get(i) == ride){
		return i;
	}
}
return -1;

